import React from 'react';
import { connect } from 'react-redux';

import CollectionItem from '../../components/cart-item/cart-item.component';

import { selectCollection } from '../../redux/shop/shop.selectors';

import './collection.styles.scss';

const CollectionPage = ({ collection }) => {
    const { title, items } = collection;

    return(
        <div className='collection'>
            <h2 className='title'>{ title }</h2>
            <div className='items'>
                {
                    items.map(item => (
                        <CollectionItem item={item}/>
                        )
                    )
                }
            </div>
        </div>
    );
};

//TODO: study where I can use ownProps in my lendit application
//TODO: selectCollecntion() is a function that returns a function (curried function) and we are passing (state) to it
const mapStateToProps = (state, ownProps) => ({
    collection: selectCollection(ownProps.match.params.collectionId)(state)
});

export default connect(mapStateToProps)(CollectionPage);
